-- Create the martial arts table with additional columns
CREATE TABLE martial_arts (
    id NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    name VARCHAR2(50) NOT NULL,
    estimated_age NUMBER CHECK (estimated_age >= 0),
    deadliness_rating NUMBER CHECK (deadliness_rating BETWEEN 1 AND 10),
    variations_flag NUMBER CHECK (variations_flag IN (0, 1)),
    difficulty_rating NUMBER CHECK (difficulty_rating BETWEEN 1 AND 10),
    years_to_master NUMBER CHECK (years_to_master >= 0),
    origin_country VARCHAR2(50),
    popularity_rating NUMBER CHECK (popularity_rating BETWEEN 1 AND 10),
    training_intensity VARCHAR2(20),
    common_techniques VARCHAR2(255),
    physical_requirements VARCHAR2(255),
    mental_requirements VARCHAR2(255),
    competition_style VARCHAR2(50),
    self_defense_effectiveness NUMBER CHECK (self_defense_effectiveness BETWEEN 1 AND 10),
    historical_significance VARCHAR2(255),
    notable_practitioners VARCHAR2(255),
    governing_body VARCHAR2(100),
    training_duration VARCHAR2(50),
    common_misconceptions VARCHAR2(255),
    famous_moves VARCHAR2(255),
    training_gear VARCHAR2(255),
    injury_risk NUMBER CHECK (injury_risk BETWEEN 1 AND 10),
    self_defense_focus NUMBER CHECK (self_defense_focus IN (0, 1)),
    cultural_significance VARCHAR2(255),
    age_of_practice NUMBER CHECK (age_of_practice >= 0),
    common_myths VARCHAR2(255),
    training_methods VARCHAR2(255),
    competition_format VARCHAR2(50),
    community_involvement VARCHAR2(255)
);

